nGUI(){						EditorGUILayout.HelpBox("By default to prevent bloat, Emoji images are not included in the project. "+								"Either add your own to Resources/Characters/ (e.g. 1f004.png by default), or download the Phantom Open Emoji set below.",MessageType.Info);						DownloadPath=EditorGUILayout.TextField("Save to..",DownloadPath);						if(IsDownloading){				GUILayout.Label("Downloading set.. ("+(Request.Progress*100f)+"%)",EditorStyles.boldLabel);			}else if(Status==1){				GUILayout.Label("Unpacking..",EditorStyles.boldLabel);			}else if(Status==2){				GUILayout.Label("Applying import settings..",EditorStyles.boldLabel);			}else if(Status==3){				GUILayout.Label("Import successful!",EditorStyles.boldLabel);			}else if(GUILayout.Button("Download Icons")){				DownloadIcons();			}					}				/// <summary>Attempts to apply import settings to images in icon path.</summary>		public static void TryImportSettings(){			// Grab the files in icon path:			string[] fileSet=Directory.GetFiles(IconPath);			int count=fileSet.Length;						if(count<=1){				return;			}						Status=3;						// Finally, update all images with the required import settings:			ImageImport.ApplyToAll(fileSet);		}				/// <summary>Downloads the icons now.</summary>		public static void DownloadIcons(){			if(IsDownloading){				return;			}			IsDownloading=true;						Request=new HttpRequest("http://powerui.kulestar.com/emoji/phantomOpenEmoji.unitypackage",OnGotIcons);						// Send it off:			Request.Send();		}				/// <summary>Gets the path that downloads will be saved to.</summary>		public static string IconPath{			get{				string result=DownloadPath.Replace("\\","/");				if(!result.EndsWith("/")){					result+="/";				}				return "Assets/"+result;			}		}				/// <summary>called when the icons have been downloaded.</summary>		public static void OnGotIcons(HttpRequest request){			IsDownloading=false;			Request=null;						if(request.Ok){							// Create the directory, if needed:				if(!Directory.Exists(IconPath)){					Directory.CreateDirectory(IconPath);				}								// Write out the bytes:				RemovePackage();								File.WriteAllBytes(IconPath+"phantomOpenEmoji.unitypackage",request.Bytes);				// Save the changes:				AssetDatabase.Refresh();								Status=1;				if(Window!=null){					Window.Repaint();				}								// Next, pull in the package:				AssetDatabase.ImportPackage(IconPath+"phantomOpenEmoji.unitypackage",false);								// Next, remove it:				RemovePackage();								// Make sure the database is up to date:				AssetDatabase.Refresh();				// And save all the changes:				AssetDatabase.SaveAssets();								Status=2;				if(Window!=null){					Window.Repaint();				}							}else{				Debug.LogError("HTTP Error getting "+request.Url+": "+request.Error);			}						if(Window!=null){				Window.Repaint();			}		}				/// <summary>Deletes the downloaded package if it exists.</summary>		public static void RemovePackage(){			if(File.Exists(IconPath+"phantomOpenEmoji.unitypackage")){				// It exists - delete it:				AssetDatabase.DeleteAsset(IconPath+"phantomOpenEmoji.unitypackage");				// Save the asset changes:				AssetDatabase.Refresh();			}		}			}	}