l elements that this css selector affects and call RecomputeRequired on them.		}				/// <summary>Gets the type of element that this selector affects.</summary>		public Css.SelectorType SelectorType{			get{								Css.SelectorType type=Css.SelectorType.Tag;								if(Selector.Length>0){					char first=Selector[0];										if(first=='.'){						type=Css.SelectorType.Class;					}else if(first=='#'){						type=Css.SelectorType.ID;					}								}								return type;			}		}				/// <summary>Does this selector have a :modifier or not?</summary>		public bool Unmodified{			get{				return (Modifier==null);			}		}				/// <summary>Called when this style is done loading.</summary>		public void OnLoaded(){					if(Selector=="@font-face"){								// Grab the src:				Value src=this["src"];								if(src==null || src.Text==null){					return;				}								string[] pieces=src.Text.Split(',');								// If pieces contain format(..), pick ttf or otf based on that.				// Otherwise go by the filetype.								string path=pieces[0].Replace("url(","").Replace(")","");								if(path.Contains("format") || path.Contains("woff")){					return;				}								DataPackage font=new DataPackage(path,Document.basepath);				font.Get(FontFaceReady);							}					}				private void FontFaceReady(DataPackage font){						// Load and apply additional settings:						if(font.Errored){								Wrench.Log.Add("@font-face error: "+font.Error);								return;			}						// Load the face - it will by default add to it's "native" family:			FontFace loaded=FontLoader.Load(font.Data);						if(loaded==null || loaded.Family==null){				Wrench.Log.Add("@font-face error: invalid font file at "+font.Url);				return;			}						Value family=this["font-family"];						// Grab the family name:			string familyName;						if(family==null || family.Text==null){							familyName=loaded.Family.Name;						}else{								familyName=family.Text.Trim().Replace("\"","");							}						// Add as an active font:			Dictionary<string,DynamicFont> fonts=Document.ActiveFonts;						DynamicFont dFont;			if(!fonts.TryGetValue(familyName,out dFont)){								// Create the font object:				dFont=new DynamicFont(familyName);				dFont.Family=loaded.Family;								fonts[familyName]=dFont;							}else{								// Hook up the "real" family:				dFont.Family=loaded.Family;								// Tell all instances using this dFont that the font is ready:				Document.html.FontLoaded(dFont);							}					}				public void BakeToModifiers(){						// Does it have any modified selectors? If so, add these properties in there too 			// (as the modified form 'derives' from this) but weakly - don't override them.			// This occurs if :aModified is defined before the unmodified selector.			List<Css.SelectorStyle> modifiedForms=Sheet.GetModifiers(Selector);							if(modifiedForms!=null){				// Great, it's got some! Copy this style over now:				for(int i=modifiedForms.Count-1;i>=0;i--){					SelectorStyle modifiedForm=modifiedForms[i];					CopyTo(modifiedForm,false);				}			}						if(Document!=null && Document.html!=null && Unmodified){				// Is this selector already in use?				Css.SelectorType type=SelectorType;								// Next, recurse through the DOM looking for it:				Document.html.RefreshSelector(type,BaseSelector);			}					}				/// <summary>Copies this objects properties to the other given style, overwriting existing properties if told to do so.</summary>		/// <param name="otherStyle">The style to copy this objects properties into.</param>		/// <param name="overwrite">True if existing properties should be overwriten.</param>		public void CopyTo(SelectorStyle otherStyle,bool overwrite){			if(otherStyle==null){				return;			}						foreach(KeyValuePair<CssProperty,Value> kvp in Properties){				if(!overwrite && otherStyle.Properties.ContainsKey(kvp.Key)){					continue;				}								otherStyle[kvp.Key]=kvp.Value.Copy();			}					}				/// <summary>Converts this selector to a css formatted string.</summary>		public override string ToString(){			string result=Selector+"{";						foreach(KeyValuePair<CssProperty,Value> kvp in Properties){				result+="\n\t"+kvp.Key.Name+":"+kvp.Value+";";			}						return result+"\n}";		}			}	}