of the sprite in the animation.</param>		public SPASprite(SPA animation,BinaryReader reader,int id){			ID=id;			Animation=animation;						// Read some bit flags - these give information about this frame:			byte flags=reader.ReadByte();						// Does it also have an alpha frame? 			// If it does, there are two images in this frame (alpha one second).			bool hasAlphaFrame=((flags&1)==1);						// How many frames this sprite holds:			FrameCount=reader.ReadUInt16();						// How big is the image, in bytes:			int dataSize=reader.ReadInt32();						// Setup the sprite now:			Sprite=new Texture2D(0,0);						// And load the image data:			Sprite.LoadImage(reader.ReadBytes(dataSize));			Width=Sprite.width;			Height=Sprite.height;						// Make sure it filters correctly.			// This is so we don't see parts of other frames around the edge of the image onscreen:			Sprite.filterMode=FilterMode.Point;						// Setup the scale:			TextureScale=new Vector2((float)Animation.FrameWidth/(float)Width,(float)Animation.FrameHeight/(float)Height);			VerticalFrameCount=Height/Animation.FrameHeight;						if(hasAlphaFrame){				int alphaImageSize=reader.ReadInt32();								// Setup the temporary alpha texture:				Texture2D alphaImage=new Texture2D(0,0);								// And load it's data:				alphaImage.LoadImage(reader.ReadBytes(alphaImageSize));								// Next, merge the alpha pixels into our main sprite.				Color[] spritePixels=Sprite.GetPixels();				Color[] alphaPixels=alphaImage.GetPixels();								if(spritePixels.Length!=alphaPixels.Length){					throw new Exception("Invalid SPA alpha channel image.");				}								// Set each alpha value from the grayscale of the alpha image:				for(int i=spritePixels.Length-1;i>=0;i--){					Color pixel=spritePixels[i];					pixel.a=alphaPixels[i].grayscale;					spritePixels[i]=pixel;				}								// Write the pixels back:				Sprite.SetPixels(spritePixels);			}		}				/// <summary>Sets the given sprite texture to this SPA sprite.</summary>		public void SetSprite(Texture2D sprite,int totalFrameCount){						// Set the sprite and the dimensions:			Sprite=sprite;			Width=sprite.width;			Height=sprite.height;						// Set the frame count:			FrameCount=totalFrameCount;						// Setup the scale:			TextureScale=new Vector2((float)Animation.FrameWidth/(float)Width,(float)Animation.FrameHeight/(float)Height);						// Figure out the vertical frame count:			VerticalFrameCount=Height/Animation.FrameHeight;					}			}	}